import { styled } from "styled-components";

export const Salir = styled.button`
 
  background: transparent;
  cursor: pointer;
  display: inline-block;
  border-radius: 20%;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  font-size: 1em;
  margin-right: .45em;
  margin-top: .45em;
  color: #CF5538;
  padding: .75em 1.75em;
  border: none;
  &:hover {
  background: #CF5538;
  color: white;
  text-decoration: none;
  transition: ease-in 0.2s;
}
`
